mapper.select:
SELECT M_SALT 
FROM MEMBER 
WHERE M_EMAIL = #{m_email}

mapper.update:
UPDATE MEMBER 
	SET M_SALT = #{m_salt}, 
		M_PASSWORD = #{m_password} 
WHERE M_EMAIL = #{m_email}

UPDATE MEMBER 
	SET M_NICKNAME = #{m_nickname} 
WHERE M_EMAIL = #{m_email}


UPDATE PROFILE_IMAGE 
	SET UPLOADPATH = #{uploadPath}
		,FILENAME = #{fileName}
		,UUID = #{uuid} 
WHERE M_EMAIL = #{m_email}

UPDATE PROFILE_IMAGE 
	SET FILENAME = #{fileName}
		,UPLOADPATH = #{uploadPath}
		,UUID = #{uuid}
WHERE M_EMAIL = #{m_email}
