mapper.select:
select * from ( select rownum rn, A.* from ( select * from notice
and n_title like '%'||#{n_title}||'%' and n_category like '%'||#{n_category}||'%' and n_writer like '%'||#{n_writer}||'%' and n_content like '%'||#{n_content}'%'
order by n_fixed, n_no desc ) A ) where rn between #{start} and #{end}

mapper.update:
UPDATE NOTICE
N_CATEGORY = #{n_category}, N_TITLE = #{n_title}, N_CONTENT = #{n_content}, N_FILE = #{n_file}, N_PFILE = #{n_pfile}
WHERE N_NO = #{n_no}

UPDATE NOTICE 
	SET N_FIXED = #{n_fixed} 
WHERE N_NO = #{n_no}

UPDATE NOTICE 
	SET N_HIT = #{n_hit} 
WHERE N_NO = #{n_no}
