mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT * FROM NOTICE
AND N_TITLE LIKE '%'||#{n_title}||'%' AND N_CATEGORY like '%'||#{n_category}||'%' AND N_CONTENT LIKE '%'||#{n_content}||'%' AND N_WRITER LIKE '%'||#{n_writer}||'%'
ORDER BY N_FIXED DESC, N_NO DESC ) A ) WHERE RN BETWEEN #{start} AND #{end}

SELECT * FROM ( SELECT ROWNUM RN, A.* FROM ( SELECT * FROM NOTICE
AND N_TITLE LIKE '%'||#{n_title}||'%' AND N_CONTENT LIKE '%'||#{n_content}||'%' AND N_TITLE LIKE '%'||#{n_title}||'%' OR N_CONTENT LIKE '%'||#{n_content}||'%' AND N_CATEGORY LIKE '%'||#{n_category}||'%' AND N_WRITER LIKE '%'||#{n_writer}||'%'
ORDER BY N_FIXED DESC, N_NO DESC ) A ) WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
UPDATE NOTICE
N_CATEGORY = #{n_category}, N_TITLE = #{n_title}, N_CONTENT = #{n_content}, N_FILE = #{n_file}, N_PFILE = #{n_pfile}
WHERE N_NO = #{n_no}

UPDATE NOTICE 
	SET N_FIXED = #{n_fixed} 
WHERE N_NO = #{n_no}

UPDATE NOTICE 
	SET N_HIT = #{n_hit} 
WHERE N_NO = #{n_no}
