mapper.select:
		SELECT * FROM (
			        SELECT ROWNUM AS RN, A.* FROM (
						                    SELECT * FROM BOARD_REPLY   WHERE BOARD_REPLY_BNO = #{boardReplyBno}
						                        START WITH  BOARD_REPLY_PARENT = 0 CONNECT BY PRIOR BOARD_REPLY_RNO = BOARD_REPLY_PARENT 

						                        ORDER SIBLINGS BY  BOARD_REPLY_WDATE ASC   
			           ) A 
    	) WHERE RN BETWEEN  #{start} AND #{end}

mapper.update:
UPDATE BOARD_REPLY 
				SET 
					BOARD_REPLY_CONTENT = #{boardReplyContent} 
					AND BOARD_REPLY_UDATE = SYSDATE
				WHERE 
					BOARD_REPLY_RNO = #{boardReplyRno}
