mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RN, A.* FROM ( SELECT * FROM REPAIR
AND RCATEGORY LIKE '%'||#{rcategory}||'%' AND RGRADE LIKE '%'||#{rgrade}||'%' AND RSTATUS LIKE '%'||#{rstatus}||'%' AND RTITLE LIKE '%'||#{rtitle}||'%' AND RCONTENT LIKE '%'||#{rcontent}||'%' AND M_EMAIL LIKE '%'||#{m_email}||'%'
RWDATE DESC RWDATE ASC
) A ) WHERE RN BETWEEN #{start} AND #{end}

mapper.insert:
INSERT INTO REPAIR 
	VALUES(
		(SELECT NVL(MAX(RNO)+1, 1) FROM REPAIR)
		, #{rcategory}
		, #{rtitle}
		, #{rcontent}   
		, TO_CHAR(SYSDATE, 'YY-MM-DD HH24:MI:SS')
		, ''
	    , #{rstatus}
		, #{m_email}
	    , #{rgrade}
	)
